<div class="tab-content form">    <?php $form = $this->beginWidget('CActiveForm', array(        'id' => 'user-changePassword-form',        'enableAjaxValidation' => true,        'clientOptions' => array(            'validateOnSubmit' => true,),    )); ?>    <p class="note">Fields with <span class="required">*</span> are required.</p>    <?php echo $form->errorSummary($model); ?>    <?php if (!$model->IsNewRecord)        echo $form->hiddenField($model, "id"); ?>    <div class="rows row">        <?php echo $form->labelEx($model, 'passwordNew'); ?>        <?php echo $form->passwordField($model, 'passwordNew', array('maxlength' => 32, 'class' => 'text-input small-input')); ?>        <?php echo $form->error($model, 'passwordNew'); ?>    </div>    <div class="rows row">        <label for="User_confirmPassword">Confirm new password</label>        <?php echo $form->passwordField($model, 'confirmPassword', array('maxlength' => 32, 'class' => 'text-input small-input')); ?>        <?php echo $form->error($model, 'confirmPassword'); ?>    </div>    <?php if(CCaptcha::checkRequirements()): ?>        <div class="row">		<?php echo $form->labelEx($model,'verifyCode'); ?>            <div>		<?php $this->widget('CCaptcha'); ?>                <?php echo $form->textField($model,'verifyCode'); ?>		</div>		<div class="hint">Please enter the letters as they are shown in the image above.		<br/>Letters are not case-sensitive.</div>            <?php echo $form->error($model,'verifyCode'); ?>	</div>    <?php endif; ?>    <div class="rows row buttons" style="margin-bottom: 0">        <?php echo CHtml::submitButton($model->isNewRecord ? 'Create' : 'Save', array('class' => 'button')); ?>    </div>    <?php $this->endWidget(); ?></div><!-- form -->