<?phpclass BackendController extends Controller{    public $layout = DEFAULT_BO_LAYOUT;    public function __construct($id, $module = null)    {        parent::__construct($id, $module);        if (Helper::user()->id) {            if (Helper::user()->checkAccess('Super')) {                Yii::app()->theme = BO_SUPER_ADMIN_THEME;            } elseif (Helper::user()->checkAccess('Administrators') || Helper::user()->checkAccess('User')) {                Yii::app()->theme = BO_THEME;            } else {                $this->redirect(Yii::app()->user->returnUrl);            }        } else            $this->redirect(array('/site/login'));    }}