<div class="tab-content form">    <?php $form = $this->beginWidget('CActiveForm', array(        'id'                   => 'user-changePassword-form',        'enableAjaxValidation' => true,        'clientOptions'        => array(            'validateOnSubmit' => true,        ),    )); ?>    <p class="note">Fields with <span class="required">*</span> are required.</p>    <?php echo $form->errorSummary($model); ?>    <?php if (!$model->IsNewRecord) {        echo $form->hiddenField($model, "id");    } ?>    <div class="rows row">        <?php echo $form->labelEx($model, 'password'); ?>        <?php echo $form->passwordField($model, 'password', array(            'maxlength' => 32,            'class'     => 'text-input small-input'        )); ?>        <?php echo $form->error($model, 'password'); ?>    </div>    <div class="rows row">        <?php echo $form->labelEx($model, 'passwordNew'); ?>        <?php echo $form->passwordField($model, 'passwordNew', array(            'maxlength' => 32,            'class'     => 'text-input small-input'        )); ?>        <?php echo $form->error($model, 'passwordNew'); ?>    </div>    <div class="rows row">        <label for="User_confirmPassword">Confirm new password</label>        <?php echo $form->passwordField($model, 'confirmPassword', array(            'maxlength' => 32,            'class'     => 'text-input small-input'        )); ?>        <?php echo $form->error($model, 'confirmPassword'); ?>    </div>    <div class="rows row buttons" style="margin-bottom: 0">        <?php echo CHtml::submitButton($model->isNewRecord ? 'Create' : 'Save', array('class' => 'button')); ?>    </div>    <?php $this->endWidget(); ?>    <a href="<?php echo Helper::url('/Admin/user/update', array('id' => Helper::user()->id)); ?>" title="Update your Profile">Update your Profile</a></div><!-- form -->